document.addEventListener("DOMContentLoaded",(function(){const fontForm=document.getElementById("fontForm"),fontList=document.getElementById("fontList");function addFont(title,family,url){fetch("add_font.php",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({title:title,family:family,url:url})}).then(response=>response.json()).then(data=>{data.success&&(loadFonts(),fontForm.reset(),showSuccessPopup())})}function loadFonts(){fetch("get_fonts.php").then(response=>response.json()).then(data=>{fontList.innerHTML="",data.fonts.forEach(font=>{const row=document.createElement("tr");row.innerHTML=`\n                    <td>${font.title}</td>\n                    <td>${font.family}</td>\n                    <td>${font.url}</td>\n                    <td class="actions">\n                        <button onclick="deleteFont(${font.id})">Delete</button>\n                    </td>\n                `,fontList.appendChild(row)})})}function showSuccessPopup(){const overlaySuccess=document.getElementById("overlaySuccess"),popupSuccess=document.getElementById("popupSuccess");overlaySuccess.style.display="block",popupSuccess.style.display="block"}function closeSuccessPopup(){const overlaySuccess=document.getElementById("overlaySuccess"),popupSuccess=document.getElementById("popupSuccess");overlaySuccess.style.display="none",popupSuccess.style.display="none"}fontForm.addEventListener("submit",(function(event){event.preventDefault();const fontTitle=document.getElementById("font-title").value,fontFamily=document.getElementById("font-family").value,fontUrl=document.getElementById("font-url").value;addFont(fontTitle,fontFamily,fontUrl)})),window.deleteFont=function(id){fetch("delete_font.php",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({id:id})}).then(response=>response.json()).then(data=>{data.success&&loadFonts()})},window.closeSuccessPopup=closeSuccessPopup,loadFonts()}));